import React, { useState, useEffect, useCallback } from 'react';
import { productAPI } from '../api'; 
import ProductFormModal from '../components/ProductFormModal'; 
import ProductTable from '../components/ProductTable';
import LoadingSpinner from '../components/LoadingSpinner';

const Products = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [showHidden, setShowHidden] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);

    const fetchProducts = useCallback(async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await productAPI.getAll(searchQuery, showHidden);
            setProducts(response.data.data);
        } catch (err) {
            console.error("Error fetching products:", err);
            setError("Lỗi khi tải danh sách sản phẩm.");
        } finally {
            setLoading(false);
        }
    }, [searchQuery, showHidden]);

    useEffect(() => {
        fetchProducts();
    }, [fetchProducts]);

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleToggleHidden = () => {
        setShowHidden(prev => !prev);
    };

    const handleCreateUpdate = async (productData) => {
        try {
            if (editingProduct) {
                // UPDATE
                await productAPI.update(editingProduct.ma_sp, productData);
            } else {
                // CREATE
                await productAPI.create(productData);
            }
            setShowModal(false);
            setEditingProduct(null);
            fetchProducts();
        } catch (err) {
            alert('Lỗi khi lưu sản phẩm: ' + (err.response?.data?.error || err.message));
        }
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setShowModal(true);
    };

    const handleDelete = async (ma_sp) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa sản phẩm này?')) {
            try {
                await productAPI.delete(ma_sp);
                fetchProducts();
            } catch (err) {
                alert('Lỗi khi xóa sản phẩm: ' + (err.response?.data?.error || err.message));
            }
        }
    };
    
    const handleToggleVisibility = async (ma_sp, currentVisibility) => {
        try {
            await productAPI.toggleVisibility(ma_sp, { hien_thi: !currentVisibility });
            fetchProducts();
        } catch (err) {
            alert('Lỗi khi cập nhật trạng thái hiển thị: ' + (err.response?.data?.error || err.message));
        }
    };


    return (
        <div className="container-fluid">
            <h2>Quản Lý Sản Phẩm</h2>
            <div className="d-flex justify-content-between mb-3">
                <button 
                    className="btn btn-primary" 
                    onClick={() => { setEditingProduct(null); setShowModal(true); }}
                >
                    + Thêm Sản Phẩm Mới
                </button>
            </div>

            <div className="mb-4 p-3 bg-light rounded shadow-sm">
                <input
                    type="text"
                    className="form-control mb-2"
                    placeholder="Tìm kiếm theo mã, tên sản phẩm..."
                    value={searchQuery}
                    onChange={handleSearch}
                />
                <div className="form-check">
                    <input
                        className="form-check-input"
                        type="checkbox"
                        checked={showHidden}
                        onChange={handleToggleHidden}
                        id="showHiddenCheck"
                    />
                    <label className="form-check-label" htmlFor="showHiddenCheck">
                        Hiển thị sản phẩm bị ẩn
                    </label>
                </div>
                <button className="btn btn-info mt-2" onClick={fetchProducts}>Áp dụng Lọc/Tìm kiếm</button>
            </div>


            {loading ? (
                <LoadingSpinner />
            ) : error ? (
                <div className="alert alert-danger">{error}</div>
            ) : (
                <ProductTable 
                    products={products}
                    onEdit={handleEdit}
                    onDelete={handleDelete}
                    onToggleVisibility={handleToggleVisibility}
                />
            )}

            {showModal && (
                <ProductFormModal 
                    product={editingProduct}
                    onClose={() => setShowModal(false)}
                    onSubmit={handleCreateUpdate}
                />
            )}
        </div>
    );
};

export default Products;
