import React, { useState, useEffect } from 'react';
import { productAPI } from '../services/api';
import './Products.css';

const Products = () => {
ย const [products, setProducts] = useState([]);
ย const [loading, setLoading] = useState(true);
ย const [search, setSearch] = useState('');
ย const [showHidden, setShowHidden] = useState(false);
ย const [showModal, setShowModal] = useState(false); 
ย const [editingProduct, setEditingProduct] = useState(null);
ย const [formData, setFormData] = useState({
ย ย ten_sp: '',
ย ย gia_ban: '',
ย ย so_luong_ton: '',
ย ย mo_ta: '',
ย ย anh: '',
ย });
ย const [alert, setAlert] = useState({ type: '', message: '' });



ย useEffect(() => {
ย ย loadProducts();
ย ย // eslint-disable-next-line react-hooks/exhaustive-deps
ย }, [search, showHidden]);

ย const loadProducts = async () => {
ย ย try {
ย ย ย setLoading(true);
ย ย ย const response = await productAPI.getAll(search, showHidden);
ย ย ย if (response.data.success) {
ย ย ย ย setProducts(response.data.data);
ย ย ย }
ย ย } catch (error) {
ย ย ย showAlert('error', 'Lแปi khi tแบฃi danh sรกch sแบฃn phแบฉm');
ย ย } finally {
ย ย ย setLoading(false);
ย ย }
ย };

ย const showAlert = (type, message) => {
ย ย setAlert({ type, message });
ย ย setTimeout(() => setAlert({ type: '', message: '' }), 3000);
ย };

ย // Hรm mแป Modal 
ย const handleOpenModal = (product = null) => {
ย ย if (product) {
ย ย ย setEditingProduct(product);
ย ย ย setFormData({
ย ย ย ย ten_sp: product.ten_sp || '',
ย ย ย ย gia_ban: product.gia_ban || '',
ย ย ย ย so_luong_ton: product.so_luong_ton || '',
ย ย ย ย mo_ta: product.mo_ta || '',
ย ย ย ย anh: product.anh || '',
ย ย ย });
ย ย } else {
ย ย ย setEditingProduct(null);
ย ย ย setFormData({
ย ย ย ย ten_sp: '',
ย ย ย ย gia_ban: '',
ย ย ย ย so_luong_ton: '0',
ย ย ย ย mo_ta: '',
ย ย ย ย anh: '',
ย ย ย });
ย ย }
ย ย setShowModal(true); 
ย };

ย const handleCloseModal = () => {
ย ย setShowModal(false);
ย ย setEditingProduct(null);
ย ย setFormData({
ย ย ย ten_sp: '',
ย ย ย gia_ban: '',
ย ย ย so_luong_ton: '',
ย ย ย mo_ta: '',
ย ย ย anh: '',
ย ย });
ย };

ย const handleSubmit = async (e) => {
ย ย e.preventDefault();
ย ย 
ย ย // Validation bแป sung
ย ย if (parseFloat(formData.gia_ban) <= 0) {
ย ย ย showAlert('error', 'Giรก bรกn phแบฃi lแปn hฦกn 0');
ย ย ย return;
ย ย }
ย ย 
ย ย // Kiแปm tra sแป lฦฐแปฃng tแปn kho chแป รกp dแปฅng cho Thรชm mแปi
ย ย if (!editingProduct && parseFloat(formData.so_luong_ton || 0) < 0) {
ย ย ย showAlert('error', 'Sแป lฦฐแปฃng tแปn khรดng ฤฦฐแปฃc รขm');
ย ย ย return;
ย ย }
ย ย 
ย ย try {
ย ย ย if (editingProduct) {
ย ย ย ย // Khi sแปญa: Loแบกi bแป so_luong_ton ฤแป khรดng gแปญi lรชn, theo logic cแปงa bแบกn
ย ย ย ย const updateData = { ...formData };
ย ย ย ย delete updateData.so_luong_ton; 
ย ย ย ย await productAPI.update(editingProduct.ma_sp, updateData);
ย ย ย ย showAlert('success', 'Cแบญp nhแบญt sแบฃn phแบฉm thรnh cรดng');
ย ย ย } else {
ย ย ย ย await productAPI.create(formData);
ย ย ย ย showAlert('success', 'Thรชm sแบฃn phแบฉm thรnh cรดng');
ย ย ย }
ย ย ย handleCloseModal();
ย ย ย loadProducts();
ย ย } catch (error) {
ย ย ย showAlert('error', error.response?.data?.error || 'Cรณ lแปi xแบฃy ra');
ย ย }
ย };

ย const handleToggleVisibility = async (product) => {
ย ย try {
ย ย ย const newStatus = product.trang_thai === 1 ? 0 : 1;
ย ย ย await productAPI.toggleVisibility(product.ma_sp, { trang_thai: newStatus });
ย ย ย showAlert('success', `ฤรฃ ${newStatus === 0 ? 'แบฉn' : 'hiแปn'} sแบฃn phแบฉm`);
ย ย ย loadProducts();
ย ย } catch (error) {
ย ย ย showAlert('error', error.response?.data?.error || 'Cรณ lแปi xแบฃy ra');
ย ย }
ย };

ย const handleDelete = async (product) => {
ย ย if (window.confirm(`Bแบกn cรณ chแบฏc muแปn xรณa sแบฃn phแบฉm ${product.ten_sp}?\n\nLฦฐu รฝ: Chแป cรณ thแป xรณa sแบฃn phแบฉm chฦฐa cรณ trong ฤฦกn hรng hoแบทc phiแบฟu nhแบญp.`)) {
ย ย ย try {
ย ย ย ย await productAPI.delete(product.ma_sp);
ย ย ย ย showAlert('success', 'Xรณa sแบฃn phแบฉm thรnh cรดng');
ย ย ย ย loadProducts();
ย ย ย } catch (error) {
ย ย ย ย showAlert('error', error.response?.data?.error || 'Cรณ lแปi xแบฃy ra');
ย ย ย }
ย ย }
ย };

ย if (loading) {
ย ย return <div className="spinner"></div>;
ย }

ย return (
ย ย <div className="products-page">
ย ย ย <div className="card">
ย ย ย ย <div className="card-header">
ย ย ย ย ย <h2>Quแบฃn Lรฝ Sแบฃn Phแบฉm</h2>
ย ย ย ย ย <button 
ย ย ย ย ย ย className="btn" 
ย ย ย ย ย ย style={{ backgroundColor: '#ff69b4', color: 'white' }} 
ย ย ย ย ย ย onClick={() => handleOpenModal()}
ย ย ย ย ย >
ย ย ย ย ย ย + Thรชm Sแบฃn Phแบฉm
ย ย ย ย ย </button>
ย ย ย ย </div>

ย ย ย ย {alert.message && (
ย ย ย ย ย <div className={`alert alert-${alert.type}`}>
ย ย ย ย ย ย {alert.message}
ย ย ย ย ย </div>
ย ย ย ย )}

ย ย ย ย <div className="search-bar">
ย ย ย ย ย <input
ย ย ย ย ย ย type="text"
ย ย ย ย ย ย placeholder="Tรฌm kiแบฟm sแบฃn phแบฉm (Mรฃ, Tรชn)..."
ย ย ย ย ย ย value={search}
ย ย ย ย ย ย onChange={(e) => setSearch(e.target.value)}
ย ย ย ย ย />
ย ย ย ย ย 
ย ย ย ย ย <div className="toggle-switch-group"> 
ย ย ย ย ย ย <label className="toggle-switch-label">Hiแปn thแป SP ฤรฃ แบฉn</label>
ย ย ย ย ย ย <label className="toggle-switch">
ย ย ย ย ย ย ย <input
ย ย ย ย ย ย ย ย type="checkbox"
ย ย ย ย ย ย ย ย checked={showHidden}
ย ย ย ย ย ย ย ย onChange={(e) => setShowHidden(e.target.checked)}
ย ย ย ย ย ย ย />
ย ย ย ย ย ย ย <span className="slider round"></span>
ย ย ย ย ย ย </label>
ย ย ย ย ย </div>
ย ย ย ย </div>

				<div className="table-container">
					<table><thead>
ย ย ย ย ย ย ย <tr>
ย ย ย ย ย ย ย ย <th>Mรฃ SP</th>
ย ย ย ย ย ย ย ย <th>Tรชn Sแบฃn Phแบฉm</th>
ย ย ย ย ย ย ย ย <th>Giรก Bรกn</th>
ย ย ย ย ย ย ย ย <th>Tแปn Kho</th>
ย ย ย ย ย ย ย ย <th>Trแบกng Thรกi</th>
ย ย ย ย ย ย ย ย <th style={{ width: '120px', textAlign: 'center' }}>Thao Tรกc</th> 
ย ย ย ย ย ย ย </tr>
			</thead><tbody>
ย ย ย ย ย ย ย {products.length === 0 ? (
ย ย ย ย ย ย ย ย <tr>
ย ย ย ย ย ย ย ย ย <td colSpan="6" style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>
ย ย ย ย ย ย ย ย ย ย Khรดng cรณ sแบฃn phแบฉm nรo ฤฦฐแปฃc tรฌm thแบฅy.
ย ย ย ย ย ย ย ย ย </td>
ย ย ย ย ย ย ย ย </tr>
ย ย ย ย ย ย ย ) : (
ย ย ย ย ย ย ย ย products.map((product) => (
ย ย ย ย ย ย ย ย ย <tr key={product.ma_sp} style={{ opacity: product.trang_thai === 0 ? 0.6 : 1 }}>
ย ย ย ย ย ย ย ย ย ย <td>
ย ย ย ย ย ย ย ย ย ย ย {product.ma_sp || '--'}
ย ย ย ย ย ย ย ย ย ย </td> 
ย ย ย ย ย ย ย ย ย ย <td>{product.ten_sp}</td>
ย ย ย ย ย ย ย ย ย ย <td>{parseInt(product.gia_ban || 0).toLocaleString('vi-VN')} โซ</td> 
ย ย ย ย ย ย ย ย ย ย <td>
ย ย ย ย ย ย ย ย ย ย ย <span className={
ย ย ย ย ย ย ย ย ย ย ย ย (product.so_luong_ton || 0) < 10 ? 'out-of-stock' : (product.so_luong_ton || 0) < 50 ? 'low-stock' : ''
ย ย ย ย ย ย ย ย ย ย ย }>
ย ย ย ย ย ย ย ย ย ย ย ย {product.so_luong_ton || 0}
ย ย ย ย ย ย ย ย ย ย ย </span>
ย ย ย ย ย ย ย ย ย ย </td>
ย ย ย ย ย ย ย ย ย ย <td>
ย ย ย ย ย ย ย ย ย ย ย <span className={`badge ${(product.trang_thai === 1 || product.trang_thai === '1') ? 'badge-success' : 'badge-danger'}`}>
ย ย ย ย ย ย ย ย ย ย ย ย {(product.trang_thai === 1 || product.trang_thai === '1') ? 'HIแปN THแป' : 'ฤร แบจN'}
ย ย ย ย ย ย ย ย ย ย ย </span>
ย ย ย ย ย ย ย ย ย ย </td>
ย ย ย ย ย ย ย ย ย ย <td style={{ textAlign: 'center' }}>
ย ย ย ย ย ย ย ย ย ย ย <div className="action-icons"> 
ย ย ย ย ย ย ย ย ย ย ย ย <button
ย ย ย ย ย ย ย ย ย ย ย ย ย className="btn-icon btn-warning-soft"
ย ย ย ย ย ย ย ย ย ย ย ย ย title="Sแปญa"
ย ย ย ย ย ย ย ย ย ย ย ย ย onClick={() => handleOpenModal(product)}
ย ย ย ย ย ย ย ย ย ย ย ย >
ย ย ย ย ย ย ย ย ย ย ย ย ย โ๏ธ
ย ย ย ย ย ย ย ย ย ย ย ย </button>
ย ย ย ย ย ย ย ย ย ย ย ย <button
ย ย ย ย ย ย ย ย ย ย ย ย ย className={`btn-icon ${(product.trang_thai === 1 || product.trang_thai === '1') ? 'btn-secondary-soft' : 'btn-success-soft'}`}
ย ย ย ย ย ย ย ย ย ย ย ย ย title={(product.trang_thai === 1 || product.trang_thai === '1') ? 'แบจn sแบฃn phแบฉm' : 'Hiแปn sแบฃn phแบฉm'}
ย ย ย ย ย ย ย ย ย ย ย ย ย onClick={() => handleToggleVisibility(product)}
ย ย ย ย ย ย ย ย ย ย ย ย >
ย ย ย ย ย ย ย ย ย ย ย ย ย {(product.trang_thai === 1 || product.trang_thai === '1') ? '๐๏ธโ๐จ๏ธ' : '๐๏ธ'}
ย ย ย ย ย ย ย ย ย ย ย ย </button>
ย ย ย ย ย ย ย ย ย ย ย ย <button
ย ย ย ย ย ย ย ย ย ย ย ย ย className="btn-icon btn-danger-soft"
ย ย ย ย ย ย ย ย ย ย ย ย ย title="Xรณa"
ย ย ย ย ย ย ย ย ย ย ย ย ย onClick={() => handleDelete(product)}
ย ย ย ย ย ย ย ย ย ย ย ย >
