import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Layout from './components/Layout';
import Products from './pages/Products';
import Customers from './pages/Customers';
import Orders from './pages/Orders';
import Stock from './pages/Stock';
import Reports from './pages/Reports';
import Login from './pages/Login';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  //  logic kiểm tra đăng nhập
  useEffect(() => {
    const checkLogin = () => {
      const token = localStorage.getItem('token');
      const savedUser = localStorage.getItem('user');
      if (token && savedUser) {
        setUser(JSON.parse(savedUser));
      }
      setLoading(false);
    };
    checkLogin();
  }, []);

  const handleLoginSuccess = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  if (loading) return null; 
  return (
    <Router>
      <Routes>
       
        <Route
          path="/login"
          element={
            // Nếu đã đăng nhập, chuyển hướng đến trang chính
            user ? <Navigate to="/products" replace /> : <Login onLoginSuccess={handleLoginSuccess} />
          }
        />

        {/* 2. Tuyến đường BẢO VỆ (Sử dụng Layout cho tất cả các trang chính) */}
        <Route
          path="/*" // Match tất cả các path khác
          element={
            // Nếu CHƯA đăng nhập, chuyển hướng đến /login
            !user ? (
              <Navigate to="/login" replace />
            ) : (
              // Nếu ĐÃ đăng nhập, hiển thị Layout và các tuyến đường con
              <Layout onLogout={handleLogout} user={user}>
                <Routes>
                  {/* Tuyến đường mặc định */}
                  <Route path="/" element={<Navigate to="/products" />} />
                  
                  {/* Các tuyến đường chính */}
                  <Route path="products" element={<Products />} /> 
                  <Route path="customers" element={<Customers />} />
                  <Route path="orders" element={<Orders />} />
                  <Route path="stock" element={<Stock />} />
                  <Route path="reports" element={<Reports />} />
                  
                  
                  <Route path="*" element={<Navigate to="/products" replace />} /> 
                </Routes>
              </Layout>
            )
          }
        />
        
        
        <Route path="/" element={<Navigate to="/products" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
