 import axios from 'axios';



const API_BASE_URL = 'http://localhost:3000/api';



const api = axios.create({

  baseURL: API_BASE_URL,

  headers: {

    'Content-Type': 'application/json',

  },

});



api.interceptors.request.use((config) => {

  const token = localStorage.getItem('token');

  if (token) {

    config.headers.Authorization = `Bearer ${token}`;

  }

  return config;

});





const buildQueryString = (params) => {

   

    const cleanParams = Object.keys(params).reduce((acc, key) => {

        // ⭐ CẬP NHẬT: Loại bỏ các giá trị rỗng, null, undefined VÀ GIÁ TRỊ FALSE (boolean)

        if (params[key] !== '' && params[key] !== null && params[key] !== undefined && params[key] !== false) {

            acc[key] = params[key];

        }

        return acc;

    }, {});

   

    return cleanParams;

};





export const authAPI = {

  login: (data) => api.post('/auth/login', data),

  register: (data) => api.post('/auth/register', data),

};



// Products API

export const productAPI = {

  getAll: (search = '', showHidden = false) => {

    const params = buildQueryString({ search, showHidden });

    return api.get('/products', { params });

  },

  create: (data) => api.post('/products', data),

  update: (id, data) => api.put(`/products/${id}`, data),

  toggleVisibility: (id, data) => api.patch(`/products/${id}/visibility`, data),

  delete: (id) => api.delete(`/products/${id}`),

};



// Customers API

export const customerAPI = {

  getAll: (search = '') =>

    api.get('/customers', { params: { search } }),

  create: (data) => api.post('/customers', data),

  update: (id, data) => api.put(`/customers/${id}`, data),

  delete: (id) => api.delete(`/customers/${id}`),

};



// Orders API

export const orderAPI = {

  getAll: (params = {}) => {

    const cleanParams = buildQueryString(params);

    return api.get('/orders', { params: cleanParams });

  },

  getById: (id) => api.get(`/orders/${id}`),

  getByCustomer: (customerId) => api.get(`/orders/customer/${customerId}`),

  create: (data) => api.post('/orders', data),

  updateStatus: (id, status) => api.put(`/orders/${id}`, { status }),

  cancel: (ma_dh) => axios.put(`/orders/cancel/${ma_dh}`),

  delete: (id) => api.delete(`/orders/${id}`),

};



// Stock API

export const stockAPI = {

  createImport: (data) => api.post('/stock/imports', data),

  // CẬP NHẬT: Nhận đối tượng params (cho startDate, endDate)

  getImports: (params = {}) => {

    const cleanParams = buildQueryString(params);

    return api.get('/stock/imports', { params: cleanParams });

  },

};



// Reports API

export const reportAPI = {

  getCurrentStock: () => api.get('/reports/stock/current'),

  getStockByDate: (date) => api.get('/reports/stock/date', { params: { date } }),

  getCustomerHistory: (customerId) => api.get(`/reports/customer/${customerId}/history`),

  getRevenueByDate: (date) => api.get('/reports/revenue/date', { params: { date } }),

  getTopSelling: () => api.get('/reports/top-selling'),

};



export default api;
