import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Layout.css';
// Thêm biểu tượng (Icon) từ thư viện React Icons (nếu bạn đã cài đặt: npm install react-icons)
// Nếu chưa cài, bạn có thể xóa import này hoặc cài đặt.
import { FiPackage, FiUsers, FiShoppingCart, FiTruck, FiBarChart2, FiLogOut } from 'react-icons/fi';

const navItems = [
  { path: '/', label: 'Sản Phẩm', icon: FiPackage },
  { path: '/customers', label: 'Khách Hàng', icon: FiUsers },
  { path: '/orders', label: 'Đơn Hàng', icon: FiShoppingCart },
  { path: '/stock', label: 'Nhập Kho', icon: FiTruck },
  { path: '/reports', label: 'Thống Kê', icon: FiBarChart2 },
];

const Layout = ({ children, user, onLogout }) => {
  const location = useLocation();
  
  // Hàm kiểm tra đường dẫn đang hoạt động
  const isActive = (path) => {
    // Xử lý trường hợp Trang chủ (/) và /products cùng kích hoạt
    if (path === '/') return location.pathname === '/' || location.pathname === '/products';
    return location.pathname.startsWith(path);
  };

  return (
    <div className="layout">
      {/* Cập nhật class navbar để sử dụng kiểu dáng Xanh Pastel */}
      <nav className="navbar">
        {/* 1. Logo/Tên thương hiệu */}
        <div className="nav-brand">
          <h1>Quản Lý Cửa Hàng</h1>
        </div>
        
        {/* 2. Menu chính (Canh giữa) */}
        <ul className="nav-menu">
          {navItems.map((item) => {
            const Icon = item.icon;
            return (
              <li key={item.path}>
                <Link 
                  to={item.path} 
                  className={isActive(item.path) ? 'active' : ''}
                  // Thêm tooltip để thân thiện hơn với người dùng
                  title={item.label}
                >
                  <Icon size={20} className="nav-icon" />
                  <span className="nav-label">{item.label}</span>
                </Link>
              </li>
            );
          })}
        </ul>

        {/* 3. Phần User & Đăng xuất (Canh phải) */}
        {user && (
          <div className="nav-user">
            <span className="user-greeting">
              Xin chào, **{user.full_name || user.username}**
            </span>
            <button 
              onClick={onLogout} 
              className="btn-logout"
              title="Đăng Xuất"
            >
              <FiLogOut size={20} style={{ marginRight: '0.25rem' }} />
              Đăng Xuất
            </button>
          </div>
        )}
      </nav>

      <main className="main-content">
        {children}
      </main>
    </div>
  );
};

export default Layout;
