import React from 'react';
import './Layout.css'; // Import CSS chung đã chỉnh sửa

// Giả định bạn đang sử dụng React Router
const Layout = ({ children }) => {
    // Giả định người dùng đã đăng nhập
    const user = { name: "Admin", role: "Quản lý" }; 
    
    const handleLogout = () => {
        console.log("Thực hiện Logout...");
        // Logic đăng xuất thực tế (xóa token, chuyển hướng)
    };

    return (
        <div className="layout">
            {/* -------------------- Navbar -------------------- */}
            <header className="navbar">
                <div className="nav-brand">
                    <h1>✨ Pastel Dashboard</h1>
                </div>
                <nav>
                    <ul className="nav-menu">
                        <li><a href="/" className="active">Trang Chủ</a></li>
                        <li><a href="/orders">Đơn Hàng</a></li>
                        <li><a href="/products">Sản Phẩm</a></li>
                        <li><a href="/reports">Báo Cáo</a></li>
                    </ul>
                </nav>
                <div className="nav-user">
                    <span className="user-greeting">Xin chào, <strong>{user.name}</strong> ({user.role})</span>
                    <button className="btn-logout" onClick={handleLogout}>
                        Đăng Xuất
                    </button>
                </div>
            </header>

            {/* -------------------- Main Content (Nơi chứa các trang cụ thể) -------------------- */}
            <main className="main-content">
                {children}
            </main>

            {/* -------------------- Footer (Optional) -------------------- */}
            {/* Nếu cần Footer, bạn có thể thêm vào đây */}
            {/* <footer className="footer">...</footer> */}
        </div>
    );
};

export default Layout;
