import React, { useState, useEffect } from 'react';
import { reportAPI, customerAPI, productAPI } from '../services/api';
import './Reports.css';

const Reports = () => {
Â  const [currentStock, setCurrentStock] = useState([]);
Â  const [stockByDate, setStockByDate] = useState([]);
Â  const [customerHistory, setCustomerHistory] = useState(null);
Â  const [customers, setCustomers] = useState([]);
Â  const [products, setProducts] = useState([]);
Â  const today = new Date().toISOString().split('T')[0]; 
Â  const [selectedDate, setSelectedDate] = useState(today);
Â  const [filterDate, setFilterDate] = useState(today); 
Â  const [selectedCustomer, setSelectedCustomer] = useState('');
Â  const [loading, setLoading] = useState(true);
Â  const [activeTab, setActiveTab] = useState('stock');

  useEffect(() => {
    loadCustomers();
    loadProducts();
  }, []);

  useEffect(() => {
    if (products.length > 0 || activeTab === 'stock') {
      loadCurrentStock();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [products, activeTab]);

  const loadProducts = async () => {
    try {
      const response = await productAPI.getAll('', true);
      if (response.data.success) {
        setProducts(response.data.data);
      }
    } catch (error) {
      console.error('Error loading products:', error);
    }
  };

 useEffect(() => {
Â  Â  if (activeTab === 'stock-date') {
        handleApplyStockDateFilter(filterDate);
Â  Â  }
Â  }, [activeTab]);
  useEffect(() => {
    if (activeTab === 'customer-history' && selectedCustomer) {
      loadCustomerHistory();
    }
  }, [selectedCustomer, activeTab]);
const handleApplyStockDateFilter = async (date = filterDate) => {
Â  Â  if (!date) return;
Â  Â  try {
Â  Â  Â  setLoading(true);
Â  Â  Â  const response = await reportAPI.getStockByDate(date); 
Â  Â  Â  if (response.data.success) {
        setFilterDate(date);
Â  Â  Â  Â  const stockData = response.data.data.products.map(item => ({
            ma_sp: item.ma_sp,
            ten_sp: item.ten_sp,
            so_luong_ton: item.so_luong_ton_theo_ngay,
            gia_ban: item.gia_ban
        }));
Â  Â  Â  Â  setStockByDate(stockData);
Â  Â  Â  }
Â  Â  } catch (error) {
Â  Â  Â  console.error('Error loading stock by date:', error);
Â  Â  Â  setStockByDate([]); 
Â  Â  } finally {
Â  Â  Â  setLoading(false);
Â  Â  }
Â  };
 const loadCurrentStock = async () => {
Â  Â  try {
Â  Â  Â  setLoading(true);
Â  Â  Â  const response = await reportAPI.getCurrentStock();
Â  Â  Â  if (response.data.success) {
Â  Â  Â  Â  let stockData = response.data.data.products || [];
Â  Â  Â  Â  // Giá»¯ nguyÃªn logic bá»• sung gia_ban
Â  Â  Â  Â  if (products.length > 0) {
Â  Â  Â  Â  Â  stockData = stockData.map(item => {
Â  Â  Â  Â  Â  Â  const product = products.find(p => p.ma_sp === item.ma_sp);
Â  Â  Â  Â  Â  Â  return {
Â  Â  Â  Â  Â  Â  Â  ...item,
Â  Â  Â  Â  Â  Â  Â  gia_ban: item.gia_ban || product?.gia_ban || 0
Â  Â  Â  Â  Â  Â  };
Â  Â  Â  Â  Â  });
Â  Â  Â  Â  }
Â  Â  Â  Â  setCurrentStock(stockData);
Â  Â  Â  }
Â  Â  } catch (error) {
Â  Â  Â  console.error('Error loading current stock:', error);
Â  Â  } finally {
Â  Â  Â  setLoading(false);
Â  Â  }
Â  };

  

  const loadCustomers = async () => {
    try {
      const response = await customerAPI.getAll();
      if (response.data.success) {
        setCustomers(response.data.data);
      }
    } catch (error) {
      console.error('Error loading customers:', error);
    }
  };

  const loadCustomerHistory = async () => {
    try {
      setLoading(true);
      const response = await reportAPI.getCustomerHistory(selectedCustomer);
      if (response.data.success) {
        setCustomerHistory({
          ...response.data.data.customer,
          don_hang: response.data.data.orders || []
        });
      }
    } catch (error) {
      console.error('Error loading customer history:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateTotalValue = (stockList) => {
    return stockList.reduce((total, item) => {
      const soLuong = parseInt(item.so_luong_ton) || 0;
      const giaBan = parseFloat(item.gia_ban) || 0;
      return total + (soLuong * giaBan);
    }, 0);
  };

  const formatPrice = (price) => {
    const numPrice = parseFloat(price);
    if (isNaN(numPrice) || numPrice === null || numPrice === undefined) {
      return '0';
    }
    return numPrice.toLocaleString('vi-VN');
  };

 if (loading && (activeTab === 'stock' || activeTab === 'stock-date')) {
Â  Â  return <div className="spinner"></div>;
Â  }

Â  return (
Â  Â  <div className="reports-page">
Â  Â  Â  <div className="card">
Â  Â  Â  Â  <div className="card-header">
Â  Â  Â  Â  Â  <h2>Thá»‘ng KÃª</h2>
Â  Â  Â  Â  </div>

Â  Â  Â  Â  <div className="tabs">
Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  className={`tab ${activeTab === 'stock' ? 'active' : ''}`}
Â  Â  Â  Â  Â  Â  onClick={() => setActiveTab('stock')}
Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Tá»“n Kho Hiá»‡n Táº¡i
Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  className={`tab ${activeTab === 'stock-date' ? 'active' : ''}`}
Â  Â  Â  Â  Â  Â  onClick={() => setActiveTab('stock-date')}
Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Tá»“n Kho Theo NgÃ y
Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  className={`tab ${activeTab === 'customer-history' ? 'active' : ''}`}
Â  Â  Â  Â  Â  Â  onClick={() => setActiveTab('customer-history')}
Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Lá»‹ch Sá»­ Mua HÃ ng
Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  </div>

Â  Â  Â  Â  <div className="tab-content">
Â  Â  Â  Â  Â  {activeTab === 'stock' && (
Â  Â  Â  Â  Â  Â  // ... (Tá»“n Kho Hiá»‡n Táº¡i giá»¯ nguyÃªn)
                <div className="stock-report">
                    <h3>TÃ¬nh Tráº¡ng Tá»“n Kho Hiá»‡n Táº¡i</h3>
                    <div className="table-container">
                        <table>
                            <thead>
                                <tr>
                                    <th>MÃ£ SP</th>
                                    <th>TÃªn Sáº£n Pháº©m</th>
                                    <th>Sá»‘ LÆ°á»£ng Tá»“n</th>
                                    <th>GiÃ¡ BÃ¡n</th>
                                    <th>GiÃ¡ Trá»‹ Tá»“n Kho</th>
                                </tr>
                            </thead>
                            <tbody>
                                {currentStock.length === 0 ? (
                                    <tr>
                                        <td colSpan="5" style={{ textAlign: 'center', padding: '2rem' }}>
                                            KhÃ´ng cÃ³ dá»¯ liá»‡u
                                        </td>
                                    </tr>
                                ) : (
                                    currentStock.map((item) => {
                                        const soLuong = parseInt(item.so_luong_ton) || 0;
                                        const giaBan = parseFloat(item.gia_ban) || 0;
                                        const giaTriTonKho = soLuong * giaBan;
                                        return (
                                            <tr key={item.ma_sp}>
                                                <td>{item.ma_sp}</td>
                                                <td>{item.ten_sp}</td>
                                                <td>{soLuong}</td>
                                                <td>{formatPrice(giaBan)} Ä‘</td>
                                                <td>{formatPrice(giaTriTonKho)} Ä‘</td>
                                            </tr>
                                        );
                                    })
                                )}
                            </tbody>
                            <tfoot>
                                <tr>
                                    <td colSpan="4" style={{ textAlign: 'right', fontWeight: 'bold' }}>
                                        Tá»•ng GiÃ¡ Trá»‹ Tá»“n Kho:
                                    </td>
                                    <td style={{ fontWeight: 'bold' }}>
                                        {formatPrice(calculateTotalValue(currentStock))} Ä‘
                                    </td>
                                </tr>
                            </tfoot>
                        </table>
                    </div>
                </div>
Â  Â  Â  Â  Â  )}

Â  Â  Â  Â  Â  {activeTab === 'stock-date' && (
