import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
// ... các imports khác ...

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // ... (Giữ nguyên useEffect, handleLoginSuccess, handleLogout) ...

  useEffect(() => {
    const checkLogin = () => {
      const token = localStorage.getItem('token');
      const savedUser = localStorage.getItem('user');
      if (token && savedUser) {
        setUser(JSON.parse(savedUser));
      }
      setLoading(false);
    };
    checkLogin();
  }, []);

  const handleLoginSuccess = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };
  
  if (loading) return null; // Hoặc hiện spinner

  // ⭐ ĐIỀU CHỈNH CHÍNH: Bọc TOÀN BỘ logic trong Router
  return (
    <Router>
      <Routes>
        {/* 1. Tuyến đường ĐĂNG NHẬP (Truy cập được khi CHƯA đăng nhập) */}
        <Route 
          path="/login" 
          element={
            // Nếu đã đăng nhập, chuyển hướng về /products
            user ? <Navigate to="/products" replace /> : <Login onLoginSuccess={handleLoginSuccess} />
          } 
        />

        {/* 2. Tuyến đường ĐƯỢC BẢO VỆ (Chỉ truy cập được khi ĐÃ đăng nhập) */}
        <Route 
          path="/*" // Dùng path="/*" để bọc tất cả các route còn lại
          element={
            // Nếu chưa đăng nhập, chuyển hướng về /login
            user ? (
              <Layout onLogout={handleLogout} user={user}>
                <Routes>
                  <Route path="/" element={<Navigate to="/products" />} />
                  <Route path="products" element={<Products />} /> 
                  <Route path="customers" element={<Customers />} />
                  <Route path="orders" element={<Orders />} />
                  <Route path="stock" element={<Stock />} />
                  <Route path="reports" element={<Reports />} />
                  {/* Bất kỳ path nào khác trong layout sẽ được chuyển hướng về /products (Tùy chọn) */}
                  <Route path="*" element={<Navigate to="/products" replace />} /> 
                </Routes>
              </Layout>
            ) : (
              // Nếu chưa đăng nhập, chuyển hướng đến trang Đăng nhập
              <Navigate to="/login" replace />
            )
          } 
        />

        {/* 3. Đảm bảo path root '/' cũng hoạt động */}
        <Route path="/" element={<Navigate to="/products" replace />} /> 
      </Routes>
    </Router>
  );
}

export default App;
