    }
  };

  const handleDelete = async (product) => {
    if (window.confirm(`B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m ${product.ten_sp}?\n\nL∆∞u √Ω: Ch·ªâ c√≥ th·ªÉ x√≥a s·∫£n ph·∫©m ch∆∞a c√≥ trong ƒë∆°n h√†ng ho·∫∑c phi·∫øu nh·∫≠p.`)) {
