        await productAPI.delete(product.ma_sp);
        showAlert('success', 'X√≥a s·∫£n ph·∫©m th√†nh c√¥ng');
        loadProducts();
      } catch (error) {
        showAlert('error', error.response?.data?.error || 'C√≥ l·ªói x·∫£y ra');
      }
