                          title={product.trang_thai === 1 ? '·∫®n s·∫£n ph·∫©m' : 'Hi·ªán s·∫£n ph·∫©m'}
                          onClick={() => handleToggleVisibility(product)}
                        >
                          {product.trang_thai === 1 ? 'üëÅÔ∏è‚Äçüó®Ô∏è' : 'üëÅÔ∏è'}
